const randomBase64Emoji = "data:image/png;base64,"

func notARateLimitIssue(err error) bool {
	return !strings.Contains(err.Error(), "You are being rate limited.")
}

func TestListGuildEmojis(t *testing.T) {
	client, err := createTestRequester()
	if err != nil {
		t.Skip()
		return
	}

	guildIDStr := os.Getenv(constant.DisgordTestGuildDefault)
	guildID, err := GetSnowflake(guildIDStr)
	if err != nil {
		t.Skip()
		return
	}

	emojis, err := ListGuildEmojis(client, guildID)
	if err != nil && !notARateLimitIssue(err) {
		t.Skip("rate limited")
	}
	if err != nil && notARateLimitIssue(err) {
		t.Error(err)
	}

	if len(emojis) != 1 && notARateLimitIssue(err) {
		t.Error("expected guild to have one emoji")
	}
}

func TestGetGuildEmoji(t *testing.T) {
	guildIDStr := os.Getenv(constant.DisgordTestGuildDefault)
	guildID, err := GetSnowflake(guildIDStr)
	if err != nil {
		t.Skip()
		return
	}
	emojiIDStr := os.Getenv(constant.DisgordTestGuildDefaultEmojiSnowflake)
	emojiID, err := GetSnowflake(emojiIDStr)
	if err != nil {
		t.Skip()
		return
	}

	client, err := createTestRequester()
	if err != nil {
		t.Skip()
		return
	}

	emoji, err := GetGuildEmoji(client, guildID, emojiID)
	if err != nil && !notARateLimitIssue(err) {
		t.Skip("rate limited")
	}
	if err != nil && notARateLimitIssue(err) {
		t.Error(err)
	}

	if emoji.ID != emojiID && notARateLimitIssue(err) {
		t.Error("emoji ID missmatch")
	}
}

func TestCreateAndDeleteGuildEmoji(t *testing.T) {
	var emoji *Emoji
	var err error

	guildIDStr := os.Getenv(constant.DisgordTestGuildAdmin)
	guildID, err := GetSnowflake(guildIDStr)
	if err != nil {
		t.Skip()
		return
	}

	client, err := createTestRequester()
	if err != nil {
		t.Skip()
		return
	}

	t.Run("create emoji", func(t *testing.T) {
		params := &CreateGuildEmojiParams{
			Name:  "testing4324",
			Image: randomBase64Emoji,
		}
		emoji, err = CreateGuildEmoji(client, guildID, params)
		if err != nil && !notARateLimitIssue(err) {
			t.Skip("rate limited")
		}
		if err != nil && notARateLimitIssue(err) {
			t.Error(err)
		}

		if emoji.ID.Empty() && notARateLimitIssue(err) {
			t.Error("emoji ID missing")
		}
	})

	t.Run("delete created emoji", func(t *testing.T) {
		err = DeleteGuildEmoji(client, Snowflake(guildID), emoji.ID)
		if err != nil && !notARateLimitIssue(err) {
			t.Skip("rate limited")
		}
		if err != nil && notARateLimitIssue(err) {
			t.Error(err)
		}
	})
}

func TestModifyGuildEmoji(t *testing.T) {
	var emoji *Emoji
	var err error
	newName := "asldjjkasd" // "super-emoji-ok" <- causes regex issue

	guildIDStr := os.Getenv(constant.DisgordTestGuildAdmin)
	guildID, err := GetSnowflake(guildIDStr)
	if err != nil {
		t.Skip()
		return
	}

	client, err := createTestRequester()
	if err != nil {
		t.Skip()
		return
	}

	t.Run("create emoji", func(t *testing.T) {
		params := &CreateGuildEmojiParams{
			Name:  "test6547465",
			Image: randomBase64Emoji,
		}
		emoji, err = CreateGuildEmoji(client, guildID, params)
		if err != nil && !notARateLimitIssue(err) {
			t.Skip("rate limited")
		}
		if err != nil && notARateLimitIssue(err) {
			t.Error(err)
		}

		if emoji.ID.Empty() && notARateLimitIssue(err) {
			t.Error("emoji ID missing")
		}
	})

	t.Run("modify emoji", func(t *testing.T) {
		params := &ModifyGuildEmojiParams{
			Name: newName,
		}
		_, err = ModifyGuildEmoji(client, guildID, emoji.ID, params)
		if err != nil && !notARateLimitIssue(err) {
			t.Skip("rate limited")
		}
		if err != nil && notARateLimitIssue(err) {
			t.Error(err)
		}
	})

	t.Run("delete created emoji", func(t *testing.T) {
		time.Sleep(1 * time.Second) // just ensure that this get's run
		err = DeleteGuildEmoji(client, guildID, emoji.ID)
		if err != nil && !notARateLimitIssue(err) {
			t.Skip("rate limited")
		}
		if err != nil && notARateLimitIssue(err) {
			t.Error(err)
		}
	})
}

func TestValidEmojiName(t *testing.T) {
	var emoji *Emoji
	var err error

	illegalNames := []string{
		"testing-this-thing-here",
	}

	guildIDStr := os.Getenv(constant.DisgordTestGuildAdmin)
	guildID, err := GetSnowflake(guildIDStr)
	if err != nil {
		t.Skip()
		return
	}

	client, err := createTestRequester()
	if err != nil {
		t.Skip()
		return
	}

	var mustDelete = false
	t.Run("create emoji", func(t *testing.T) {
		params := &CreateGuildEmojiParams{
			Name:  illegalNames[0],
			Image: randomBase64Emoji,
		}

		_, body, err := client.Post(&httd.Request{
			Ratelimiter: ratelimitGuild(guildID),
			Endpoint:    endpoint.GuildEmojis(guildID),
			Body:        params,
		})
		if err != nil && !notARateLimitIssue(err) {
			t.Skip("rate limited")
		}
		if err != nil {
			return
		}

		err = unmarshal(body, emoji)
		if err == nil {
			t.Error("discord does accept '-' in emoji names now. Please update validEmojiName()")
			mustDelete = true
		} else {
			t.Log(err)
		}
	})

	t.Run("delete created emoji", func(t *testing.T) {
		if !mustDelete {
			t.Skip("no new emoji created")
			return
		}
		err = DeleteGuildEmoji(client, guildID, emoji.ID)
		if err != nil && !notARateLimitIssue(err) {
			t.Skip("rate limited")
		}
		if err != nil && notARateLimitIssue(err) {
			t.Error(err)
		}
	})
}
